<script>
  import Song from './components/Song'
  import DropdownButton from './components/DropdownButton'
  import FilterCheckbox from './components/FilterCheckbox'
  import SelectedFilter from './components/SelectedFilter'
  import LoadingSpinner from './components/LoadingSpinner'
  import SortButton from './components/SortButton'
  import SortDropdown from './components/SortDropdown'
  
  import Axios from 'axios'


  
  const requestSongsFromApi = (filters) => {
	//fancy javascript one liners
		var filtersAsString = Object.keys(filters).map(filter => filter+"="+filters[filter].join(",")).join("&")
		//make the axios request with the given parameters
		return Axios.get(`https://api.sin.ga/v1.4/songs/?page_size=24&${filtersAsString}`)
	}
	const getGenres = () => {
	//fancy javascript one liners
		
		//make the axios request with the given parameters
		return Axios.get(`https://api.sin.ga/v1.4/genres/?page_size=100`)
	}
  
  export default {
    name: 'App',
    components: {
      Song,
      DropdownButton,
	  SelectedFilter,
      FilterCheckbox,
	  LoadingSpinner,
	  SortButton,
	  SortDropdown
    },
    data () {

    	return {
    		sideBarActivated: false,
    		loading: false,
    		songs: [],
    		filters: {
    			genre: [],
    			language:[],
    			sort: []
    		},
    		languages: {
    			"en":{	
    				id:"en",
    				name: "English",
    				active: false
    			},
    			"fi":{
    				id:"fi",
    				name: "Finnish",
    				active: false
    			},
    			"sv":{	
    				id:"sv",
    				name: "Swedish",
    				active: false
    			},
    			"de":{
    				id:"de",
    				name: "German",
    				active: false
    			},
    			"he":{
    				id:"he",
    				name: "Hebrew",
    				active: false
    			},
    			"it":{
    				id:"it",
    				name: "Italian",
    				active: false
    			},
    			"fr":{
    				id:"fr",
    				name: "French",
    				active: false
    			},
    			"ko":{
    				id:"ko",
    				name: "Korean",
    				active: false
    			},
				"es":{
    				id:"es",
    				name: "Spanish",
    				active: false
    			}
    		},
    		sort: {
    			"top":{	
    				id:"top",
    				name: "Popularity",
    				active: false
    			},
    			"latest":{	
    				id:"latest",
    				name: "Release Date",
    				active: false
    			},
    			"-name":{	
    				id:"-name",
    				name: "Alphabetical (Z-A)",
    				active: false
    			},
    			"name":{	
    				id:"name",
    				name: "Alphabetical (A-Z)",
    				active: true
    			}
    			
    		},
    		genres : {}		
  		}
    },
   beforeCreate () {
			getGenres().then(res => {
				res.data.results.forEach(genre => {
					this.$set(this.genres,genre.id,  {
						id: genre.id,
						name: genre.name,
						active: false
					})
				})
			})
  	},
  	created () {
  		requestSongsFromApi(this.filters).then(res=>{
    		this.songs = res.data.results
    	})

  		//a listener is set for the $emit event in FilterCheckbox.vue
  		this.$root.$on("setFilter", (value) => {
  			var obj;

  			switch (value.type) {
			    case "genre":
			        obj = this.genres;
			        break;
			    case "language":
			        obj = this.languages;
			        break;
			    case "sort":
			        obj = this.sort;
			        break;
			}
  			//set the value of language object that was emitted
  			obj[value.key].active  = value.active

  			//maps the objects to a array of language ids 
  			this.filters[value.type] = Object.keys(obj).map(lang=>obj[lang]).filter(lang=>lang.active).map(lang => lang.id)

  			//do new request
  			this.loading = true
  			requestSongsFromApi(this.filters).then(res=>{
		  		this.songs = res.data.results
		  		this.loading = false
		  	})
  		})
  		this.$root.$on("setSort", (value) => {
  			this.filters.sort = [value]
  			Object.keys(this.sort).forEach((sortKey) => {
  				if(sortKey===value){
  					this.sort[sortKey]["active"] = true
  				} else {
  					this.sort[sortKey]["active"] = false
  				}
  				
  			})
  			//do new request
  			this.loading = true
  			requestSongsFromApi(this.filters).then(res=>{
		  		this.songs = res.data.results
		  		this.loading = false
		  	})
  		})



  	},
  	computed: {
  		selectedGenres() {
  			return Object.keys(this.genres).map(key => this.genres[key]).filter(genre => genre.active)
  		},
  		selectedLanguages () {
  			return Object.keys(this.languages).map(key => this.languages[key]).filter(language => language.active)
  		},
  		selectedSorting () {
  			return Object.keys(this.sort).map(key => this.sort[key]).filter(sorting => sorting.active)
  		},

  	},
    methods: {
    	 mapImages: (item) => {
		    var im = item.image
		    var imageUrl = (im && im.medium && im.medium.url) ?
		    im.medium.url : (im && im.small && im.small.url) ? im.small.url : (im && im.tiny && im.tiny.url) ? im.tiny.url : ""
		 
		    return imageUrl
		},
    	requestSongs () {
    		this.loading = true
				requestSongsFromApi(this.filters).then(res=>{
		    		this.songs = res.data.results
		    		this.loading = false
		    	})
    	},
    	deleteFilter(type, id) {
    		const obj = value.type === "genre" ? this.genres : this.languages
  			//set the value of language object that was emitted
  			obj[id].active  = false

    	}

    }
}
